targets.expr:
sum(
    rate(
        voltdb_procedure_invoked_total
        {
            namespace="$cluster",
            host_name=~"$host",
            partition_id=~"$partition",
            procedure_name=~"$procedure"
        }[$__rate_interval]
    )
)

sum(
    rate(
        voltdb_initiator_procedure_invoked_total
        {
            namespace="$cluster",
            host_name=~"$host",
            procedure_name=~"$procedure",
            connection_origin="EXTERNAL"
        }[$__rate_interval]
))

sum by (procedure_name) (
    rate(
        voltdb_procedure_invoked_total
        {
            namespace="$cluster",
            host_name=~"$host",
            partition_id=~"$partition",
            procedure_name=~"$procedure"
        }[$__rate_interval]
    )
)

sum by (procedure_name) (rate(
        voltdb_initiator_procedure_invoked_total
        {
            namespace="$cluster",
            host_name=~"$host",
            procedure_name=~"$procedure",
            connection_origin="EXTERNAL"
        }[$__rate_interval]
))

sort(
    max by (procedure_name)
    (
        voltdb_procedure_invoked_time_seconds_max
        {
            namespace="$cluster",
            host_name=~"$host",
            partition_id=~"$partition",
            procedure_name=~"$procedure"
        }
    )
)

sum by (procedure_name)
(
    increase(voltdb_procedure_invoked_total
    {
        namespace="$cluster",
        host_name=~"$host",
        partition_id=~"$partition",
        procedure_name=~"$procedure"
    }[$__range])
)

sum by (procedure_name)
(
    voltdb_procedure_invoked_time_seconds_max
    {
        namespace="$cluster",
        host_name=~"$host",
        partition_id=~"$partition",
        procedure_name=~"$procedure"
    }
)

sort(
    max by (procedure_name)
    (
        rate(voltdb_procedure_invoked_total
        {
            namespace="$cluster",
            host_name=~"$host",
            partition_id=~"$partition",
            procedure_name=~"$procedure"
        }[$__rate_interval])
    )
)

sum by (procedure_name, host_name)
(
    voltdb_procedure_invoked_time_seconds_max
    {
        namespace="$cluster",
        host_name=~"$host",
        partition_id=~"$partition",
        procedure_name=~"$procedure"
    }
)

max by (procedure_name, host_name)
(
    voltdb_procedure_invoked_time_seconds_max
    {
        namespace="$cluster",
        host_name=~"$host",
        partition_id=~"$partition",
        procedure_name=~"$procedure"
    }
)

sum by (procedure_name, partition_id)
(
    voltdb_procedure_invoked_time_seconds_max
    {
        namespace="$cluster",
        host_name=~"$host",
        partition_id=~"$partition",
        procedure_name=~"$procedure"
    }
)
